<script>
    import CircleName from "./CircleName";

    export default {
        name: "HeroModal",
        components: {CircleName},
        data() {
            return {
                homeworldValue: '',
                speciesValue: '',
                filmsValue: []
            }
        },
        methods: {
            close() {
                this.$emit('close');

            },
            getHomeworld(){
                axios.get(this.homeworld.replace(/^http:\/\//i, 'https://'))
                    .then(response => this.homeworldValue = response.data.name)
                    .catch(error => console.log(error));
            },
            getFilms() {
                this.films.forEach(film => {
                    axios.get(film.replace(/^http:\/\//i, 'https://'))
                        .then(response => this.filmsValue.push(response.data.title))
                        .catch(error => console.log(error))
                });
            },
            getSpecies() {
                if (this.species.length > 0) {
                    this.species.forEach(specie => {
                        axios.get(specie.replace(/^http:\/\//i, 'https://'))
                            .then(response => this.speciesValue.push(response.data.name))
                            .catch(error => console.log(error))
                    })
                }
            }

        },
        props: {show: Boolean, name: String, species: Array, homeworld: String, birth_year: String, gender: String, films: Array},
        mounted () {
                this.getHomeworld();
                this.getSpecies();
                this.getFilms();
        }
    }
</script>
